#Error Catching because of input types
if (is.na(as.numeric(number_branches))) { #if it can't convert it to numeric it sets it as NA, so we need to look for that
  stop(paste0("random_branch Node Error: number_branches is not numeric. Please enter an integer between 1 and 8. number_branches is set to: ", number_branches))
}
if (is.na(as.numeric(branch_chosen))) { #if it can't convert it to numeric it sets it as NA, so we need to look for that
  stop(paste0("random_branch Node Error: branch_chosen is not numeric. Please enter an integer between 1 and 8. Alternatively, if you want to let it choose the branch randomly then leave it as the default of 0. branch_chosen is set to: ", branch_chosen))
}
number_branches = as.numeric(number_branches) #can't simply use tryCatch because as.numeric doesn't return an error; if it fails it sets it to NA
branch_chosen = as.numeric(branch_chosen)

if (number_branches > 8 | number_branches < 1 | number_branches != as.integer(number_branches)) { #Notice that I let it be 1 still, although it's obviously pointless to have it choose a random branch between 1 and 1
  stop(paste0("random_branch Node Error: number_branches is not an integer between 1 and 8. number_branches is set to: ", number_branches))
}
if (number_branches == 1) { #Give a warning if number_branches == 1
  print("random_branch Node Warning: number_branches is set to 1. This will choose a random number between 1 and 1. So it's rather pointless!")
}
if (branch_chosen > 8 | branch_chosen < 0 | branch_chosen != as.integer(branch_chosen)) {
  stop(paste0("random_branch Node Error: branch_chosen is not an integer between 1 and 8. Alternatively, if you want to let it choose the branch randomly, then leave it as the default of 0. branch_chosen is set to: ", branch_chosen))
}


if (as.numeric(branch_chosen) == 0) { #user did not set return branch from outside, so choose branch randomly
  random_number = sample(1:number_branches, 1)
  .branch = paste0("branch_", random_number)
  branch_chosen = random_number
} else { #user already told us what branch to use
  .branch = paste0("branch_", branch_chosen)
}

print(paste0(".branch = ", .branch)) #for bug checking
