getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  if(ib$table_type == "custom") {
    data_set_id = dbEscapeStrings(concerto$connection, ib$data_set_id)
    table = ib$custom_table$table
    question_column = ib$custom_table$columns$question
    order_column = ib$custom_table$columns$order_index
    trait_column = ib$custom_table$columns$trait
    reversed_score_column = ib$custom_table$columns$reversed_score
    data_set_id_column = ib$custom_table$columns$data_set_id

    questions = concerto.table.query("SELECT id, {{question_column}} AS question, {{trait_column}} AS trait, {{reversed_score_column}} AS reversed_score FROM {{table}} WHERE {{data_set_id_column}} = '{{data_set_id}}' ORDER BY {{order_column}} ASC", list(
      question_column=question_column,
      order_column=order_column,
      trait_column=trait_column,
      reversed_score_column=reversed_score_column,
      table=table,
      data_set_id_column=data_set_id_column,
      data_set_id=data_set_id
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        questions = rbind(questions, data.frame(ib$direct_table[[i]], stringsAsFactors=F))
      }
    }
  }

  if(dim(questions)[1] > 0 && order == "random") {
    questions = questions[sample(1:dim(questions)[1]),]
  }
  questions = data.frame(questions,stringsAsFactors=F)
  return(questions)
}

getContent = function(questions, current_page, response_set, not_valid, all_responses) {
  qset = list()
  rset = list()
  a = 1
  for(i in ((current_page-1)*as.numeric(items_per_page)+1):(current_page*as.numeric(items_per_page))) {
    if(i > dim(questions)[1]) { break }
    question = questions[i,]
    noAnswer = "false"
    if(question$id %in% not_valid) {
      noAnswer = "true"
    }
    qset[[a]] = list(
      id=question$id,
      question=question$question,
      noAnswer=noAnswer,
      answer=all_responses[[paste0("q",question$id)]]
    )
    a = a + 1
  }

  a = 1
  for(i in 1:dim(response_set)[1]) {
    response = response_set[i,]
    checked = ""
    rset[[a]] = list(
      label=response$label,
      value=response$value
    )
    a = a + 1
  }
  content = concerto.template.join(templateId=questionnaire_template, params=list(
    paragraph=paragraph,
    questions=toJSON(qset),
    options=toJSON(rset)
  ))
  return(content)
}

addAnswers = function(answers, response, response_set, questions, rb, all_responses, scores) {
  max_score = max(as.numeric(response_set[,"score"]))
  min_score = min(as.numeric(response_set[,"score"]))

  for(var in ls(response)) {
    if(var == "buttonPressed" || var == "timeTaken" || var == "isTimeout") { next }

    question_id = substr(var, 2, nchar(var))
    value = response[[var]]
    score = NULL
    for(i in 1:dim(questions)[1]) {
      question = questions[i,]
      if(question$id == question_id) {
        trait = toString(question$trait)
        if(is.null(scores[[trait]])) {
          scores[[trait]] = 0
        }
        for(j in 1:dim(response_set)[1]) {
          r = response_set[j,]
          if(r$value == value) {
            score = as.numeric(r$score)
            if(question$reversed_score == "1") {
              score = max_score - score + min_score
            }
            scores[[trait]] = scores[[trait]] + score
            break
          }
        }
        break
      }
    }
    saveResponse(rb, question_id, value, score, trait)
  }
  return(scores)
}

removeAnswers = function(questions, current_page, response_set, all_responses, scores) {
  max_score = max(as.numeric(response_set[,"score"]))
  min_score = min(as.numeric(response_set[,"score"]))

  for(i in ((current_page-1)*as.numeric(items_per_page)+1):(current_page*as.numeric(items_per_page))) {
    if(i > dim(questions)[1]) { break }
    question = questions[i,]
    trait = toString(question$trait)
    if(is.null(scores[[trait]])) {
      scores[[trait]] = 0
    }

    if(dim(response_set)[1] > 0) {
      for(i in 1:dim(response_set)[1]) {
        response = response_set[i,]
        if(!is.null(all_responses[[paste0("q",question$id)]]) && all_responses[[paste0("q",question$id)]] == response$value) { 
          score = as.numeric(response$score)
          if(question$reversed_score == "1") {
            score = max_score - score + min_score
          }
          scores[[trait]] = scores[[trait]] - score
        }
      }
    }
  }
  return(scores)
}

saveResponse = function(rb, question_id, value, score, trait) {
  table = "default_questionnaire_response_table"
  session_id_column = "session_id"
  question_id_column = "question_id"
  value_column = "value"
  score_column = "score"
  trait_column = "trait"
  data_set_id_column = "data_set_id"
  if(rb$table_type == "custom") {
    table = rb$custom_table$table
    session_id_column = rb$custom_table$columns$session_id
    question_id_column = rb$custom_table$columns$question_id
    value_column = rb$custom_table$columns$value
    score_column = rb$custom_table$columns$score
    trait_column = rb$custom_table$columns$trait
    data_set_id_column = rb$custom_table$columns$data_set_id
  }
  session_id = 0
  if(!is.null(session) && is.list(session)) {
    session_id = session$id
  } else {
    session_id = paste0("i",concerto$session$id)
  }
  data_set_id = rb$data_set_id

  exists = FALSE
  record = concerto.table.query("SELECT * FROM {{table}} WHERE {{session_id_column}}='{{session_id}}' AND {{question_id_column}}={{question_id}} AND {{data_set_id_column}}='{{data_set_id}}'", list(
    table=table,
    session_id_column=session_id_column,
    session_id=session_id,
    question_id_column=question_id_column,
    question_id=question_id,
    data_set_id_column=data_set_id_column,
    data_set_id=data_set_id
  ))
  if(dim(record)[1] > 0) { exists = TRUE }

  if(exists) {
    concerto.table.query("UPDATE {{table}} SET {{value_column}}='{{value}}', {{score_column}}={{score}}, {{trait_column}}='{{trait}}' WHERE {{session_id_column}}='{{session_id}}' AND {{question_id_column}}={{question_id}} AND {{data_set_id_column}}='{{data_set_id}}'", list(
      table=table,
      session_id_column=session_id_column,
      session_id=session_id,
      question_id_column=question_id_column,
      question_id=question_id,
      value_column=value_column,
      value=value,
      score_column=score_column,
      score=score,
      trait_column=trait_column,
      trait=trait,
      data_set_id_column=data_set_id_column,
      data_set_id=data_set_id
    ))
  } else {
    concerto.table.query("INSERT INTO {{table}} ({{session_id_column}}, {{question_id_column}}, {{value_column}}, {{score_column}}, {{trait_column}}, {{data_set_id_column}}) VALUES ('{{session_id}}', {{question_id}}, '{{value}}', {{score}}, '{{trait}}', '{{data_set_id}}')", list(
      table=table,
      session_id_column=session_id_column,
      session_id=session_id,
      question_id_column=question_id_column,
      question_id=question_id,
      value_column=value_column,
      value=value,
      score_column=score_column,
      score=score,
      trait_column=trait_column,
      trait=trait,
      data_set_id_column=data_set_id_column,
      data_set_id=data_set_id
    ))
  }
}

getNotValid = function(response, current_page, questions) {
  not_valid = c()
  for(i in ((current_page-1)*as.numeric(items_per_page)+1):(current_page*as.numeric(items_per_page))) {
    if(i > dim(questions)[1]) { break }
    question = questions[i,]
    if(response_required == "1" && is.null(response[[paste0("q",question$id)]])) {
      not_valid = c(not_valid, question$id)
    }
  }
  return(not_valid)
}

