library(digest)

if(is.null(template_def) || is.na(template_def) || template_def == "") {
  decoded_template_def = fromJSON(concerto.var.get("template_def"))
} else {
  decoded_template_def = fromJSON(template_def)
}

getContent = function(login_form) {
  content = login_form$paragraph

  if(!is.na(type) && type == "table") {
    login_control = concerto.template.join(templateId=login_form$form_field_input, params=list(id="login",name="login",type="text",value=""))
    password_control = concerto.template.join(templateId=login_form$form_field_input, params=list(id="password",name="password",type="password",value=""))
    login = concerto.template.join(templateId=login_form$form_field, params=list(id="login",label="login",classes="",control=login_control))
    password = concerto.template.join(templateId=login_form$form_field, params=list(id="password",label="password",classes="",control=password_control))
    content = paste0(content, login, password)
  }

  return(content)
}

authorizeUser = function(response) {
  print(paste0("authorizing user: ",response$login))
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "direct") {
      users = ub$direct_table
      if(length(users) > 0) {
        for(i in 1:length(users)) {
          user = users[[i]]
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "default") {
      users = concerto.table.query("SELECT * FROM default_user_table WHERE data_set_id = '{{data_set_id}}'", list(
        data_set_id = dbEscapeStrings(concerto$connection, toString(ub$data_set_id))
      ))
      if(dim(users)[1] > 0) {
        for(i in 1:dim(users)[1]) {
          user = as.list(users[i,])
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("SELECT * FROM {{table}} WHERE {{data_set_id_column}} = '{{data_set_id}}'", list(
        table=ub$custom_table$table, 
        data_set_id_column=ub$custom_table$columns$data_set_id, 
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id))
      ))
      if(dim(users)[1] > 0) {
        for(i in 1:dim(users)[1]) {
          user = as.list(users[i,])
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    }
  }
  return(NULL)
}

createSession = function(user) {
  sb = fromJSON(session_bank)
  user_id = NULL
  if(!is.null(user)) { user_id = user$id }

  if(sb$table_type == "default") {
    concerto.table.query("INSERT INTO default_session_table (internal_id, test_id, user_id, last_activity_time, started_time, finished) VALUES ('{{internal_id}}', {{test_id}}, '{{user_id}}', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 0)", list(
      internal_id=concerto$session$id, 
      test_id=concerto$mainTest$id, 
      user_id=user_id
    ))
    return(as.list(concerto.table.query("SELECT * FROM default_session_table WHERE id={{id}}", params=list(id=concerto.table.lastInsertId()))[1,]))
  } 
  if(sb$table_type == "custom") {
    concerto.table.query("INSERT INTO {{table}} ({{iternal_id_column}}, {{test_id_column}}, {{user_id_column}}, {{last_activity_time_column}}, {{started_time_column}}, {{finished_column}}) VALUES ('{{internal_id}}', {{test_id}}, '{{user_id}}', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 0)", list(
      table=sb$custom_table$table, 
      iternal_id_column=sb$custom_table$columns$internal_id, 
      internal_id=concerto$session$id, 
      test_id_column=sb$custom_table$columns$test_id, 
      test_id=concerto$mainTest$id, 
      user_id_column=sb$custom_table$columns$user_id, 
      user_id=user_id,
      last_activity_time_column=sb$custom_table$columns$last_activity_time,
      started_time_column=sb$custom_table$columns$started_time,
      finished_column=sb$custom_table$columns$finished
    ))
    return(as.list(concerto.table.query("SELECT * FROM {{table}} WHERE id={{id}}", params=list(table=sb$custom_table$table, id=concerto.table.lastInsertId()))[1,]))
  }
}

resumeSession = function(user) {
  sb = fromJSON(session_bank)
  user_id = NULL
  if(!is.null(user)) { user_id = user$id }

  table = "default_session_table"
  test_id_column = "test_id"
  user_id_column = "user_id"
  internal_id_column = "internal_id"
  last_activity_time_column = "last_activity_time"
  finished_column = "finished"
  if(sb$table_type == "custom") {
    table = sb$custom_table$table
    test_id_column = sb$custom_table$columns$test_id
    user_id_column = sb$custom_table$columns$user_id
    internal_id_column = sb$custom_table$columns$internal_id
    last_activity_time_column = sb$custom_table$columns$last_activity_time
    finished_column = sb$custom_table$columns$finished
  }

  session = concerto.table.query("SELECT * FROM {{table}} WHERE {{test_id_column}} = {{test_id}} AND {{user_id_column}} = '{{user_id}}' AND {{finished_column}} = 0 ORDER BY id DESC", list(
      table=table, 
      test_id_column=test_id_column, 
      test_id=concerto$mainTest$id, 
      user_id_column=user_id_column, 
      user_id=user_id,
      finished_column=finished_column
  ), n=1)
  if(dim(session)[1] == 0) {
    return(NULL)
  }
  session = as.list(session)
  session$previous_internal_id = session$internal_id
  session$internal_id = concerto$session$id

  time_limit = as.numeric(resume_session_time_limit)
  if(time_limit > 0) {
  	time_diff = as.numeric(Sys.time()) - as.numeric(strptime(session$last_activity_time, "%Y-%m-%d %H:%M:%S"))
    if(time_diff > time_limit) {
      print("session resume time limit exceeded")
      return(NULL)
    }
  }

  concerto.table.query("UPDATE {{table}} SET {{internal_id_column}}='{{internal_id}}', {{last_activity_time_column}}=CURRENT_TIMESTAMP WHERE id={{id}}", list(
    table=table,
    internal_id_column=internal_id_column,
    internal_id=concerto$session$id,
    id=session$id,
    last_activity_time_column=last_activity_time_column
  ))

  return(session)
}

doesLoginExists = function(login) {
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "default") {
      users = concerto.table.query("SELECT * FROM default_user_table WHERE data_set_id = '{{data_set_id}}' AND login = '{{login}}'", list(
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        login=dbEscapeStrings(concerto$connection, toString(login))
      ))
      if(dim(users)[1] > 0) {
        return(TRUE)
      }
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("SELECT * FROM {{table}} WHERE {{data_set_id_column}} = '{{data_set_id}}' AND {{login_column}} = '{{login}}'", list(
        table=ub$custom_table$table, 
        data_set_id_column=ub$custom_table$columns$data_set_id, 
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        login_column=ub$custom_table$columns$login, 
        login=dbEscapeStrings(concerto$connection, toString(login))
      ))
      if(dim(users)[1] > 0) {
        return(TRUE)
      }
    }
  }
  return(FALSE)
}

createNewUser = function(login, password, email, enabled) {
  print(paste0("creating new user: ",login))
  ub = fromJSON(user_bank)
  encryption = "sha1"

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "default") {
      users = concerto.table.query("INSERT INTO default_user_table (data_set_id, login, password, encryption, email, enabled) VALUES ('{{data_set_id}}', '{{login}}', '{{password}}', '{{encryption}}', '{{email}}', '{{enabled}}')", list(
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        login=dbEscapeStrings(concerto$connection, toString(login)),
        password=dbEscapeStrings(concerto$connection, toString(digest(password, encryption, serialize=F))),
        encryption=dbEscapeStrings(concerto$connection, toString(encryption)),
        email=dbEscapeStrings(concerto$connection, toString(email)),
        enabled=dbEscapeStrings(concerto$connection, toString(enabled))
      ))
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("INSERT INTO {{table}} ({{data_set_id_column}}, {{login_column}}, {{password_column}}, {{encryption_column}}, {{email_column}}, {{enabled_column}}) VALUES ('{{data_set_id}}', '{{login}}', '{{password}}', '{{encryption}}', '{{email}}', '{{enabled}}')", list(
        table=ub$custom_table$name, 
        data_set_id_column=ub$custom_table$columns$data_set_id, 
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        login_column=ub$custom_table$columns$login, 
        login=dbEscapeStrings(concerto$connection, toString(login)),
        password_column=ub$custom_table$columns$password, 
        password=dbEscapeStrings(concerto$connection, toString(digest(password, encryption, serialize=F))),
        encryption_column=ub$custom_table$columns$encryption, 
        encryption=dbEscapeStrings(concerto$connection, toString(encryption)),
        email_column=ub$custom_table$columns$email, 
        email=dbEscapeStrings(concerto$connection, toString(email)),
        enabled_column=ub$custom_table$columns$enabled, 
        enabled=dbEscapeStrings(concerto$connection, toString(enabled))
      ))
    }
    return(concerto.table.lastInsertId())
  }
  return(NULL)
}

sendConfirmationMail = function(uid, email, smtp_host, smtp_port, sender, subject, paragraph, url, template){
  library(mailR)

  url = concerto.template.insertParams(url, params=list(ecuid=uid))
  recipients = c(email)
  body = concerto.template.join(templateId=template,params=list(url=url,paragraph=paragraph))

  send.mail(
    from = sender,
    to = recipients,
    subject = subject,
    body = body,
    smtp = list(host.name = smtp_host, port = smtp_port),
    html = TRUE, 
    inline = TRUE,
    authenticate = FALSE,
    send = TRUE
  )
}

enableUser = function(uid) {
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "default") {
      users = concerto.table.query("UPDATE default_user_table SET enabled='{{enabled}}' WHERE data_set_id='{{data_set_id}}' AND id={{id}}", list(
        enabled=1,
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        id=uid
      ))
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("UPDATE {{table}} SET {{enabled_column}}='{{enabled}}' WHERE {{data_set_id_column}}='{{data_set_id}}' AND id={{id}}'", list(
        table=ub$custom_table$table, 
        enabled_column=ub$custom_table$columns$enabled, 
        enabled=1,
        data_set_id_column=ub$custom_table$columns$data_set_id, 
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        id=uid
      ))
    }
  }
}

