        result = concerto.test.run(registration$form_test, params=list(
          template=decoded_template_def$layout,
          header=decoded_template_def$header,
          title=registration_form$title, 
          paragraph=registration_form$paragraph,
          footer=decoded_template_def$footer,
          form=toJSON(
            list(
              list(name="login", definition=list(type="singleline"), label=registration_form$login_label, validation="login!=''", alert=registration_form$login_alert),
              list(name="password", definition=list(type="password"), label=registration_form$password_label, validation="password!=''", alert=registration_form$password_alert),
              list(name="password_confirmation", definition=list(type="password"), label=registration_form$password_confirmation_label, validation="password==password_confirmation", alert=registration_form$password_confirmation_alert),
              list(name="email", definition=list(type="singleline"), label=registration_form$email_label, validation="email!=''", alert=registration_form$email_alert)
            )
          ),
          warning_msg = reg_warning_msg,
          button_label=registration_form$register_button_label
        ));

        if(!doesLoginExists(result$response$login)) {
          break
        } else {
          reg_warning_msg = registration_form$login_exists_alert
        }
      }
      email_confirmation_on = registration$email_confirmation == "1"
