collection.css:
@import url("https://fonts.googleapis.com/css?family=Roboto");

body {
  color: #2c343a;
  font-family: "Roboto";
  font-style: normal;
  font-weight: 400;
}
p {
  margin: 10px 0 10px;
}
h1 {
  color: #2c343a;
  font-family: "Roboto";
  font-size: 1.5em;
  font-style: normal;
  font-weight: 500;
  margin: 0 auto;
  padding-bottom: 10px;
  padding-top: 10px;
  max-width: 900px;
}
h2 {
  color: #2c343a;
  font-family: "Roboto";
  font-size: 1.3em;
  font-style: normal;
  font-weight: 500;
  margin-bottom: 10px;
  margin-top: 0;
}
h3 {
  color: #2c343a;
  font-family: "Roboto";
  font-size: 1.2em;
  font-style: normal;
  font-weight: 500;
  margin-bottom: 10px;
  margin-top: 0;
}
a:link {
  color: #000000;
  text-decoration: underline;
}
a:visited {
  color: #000000;
  text-decoration: underline;
}
a:hover {
  color: #000000;
  text-decoration: underline;
}
ol, ul, li {
  padding-bottom: 10px;
}
table.decorated {
  border: 1px solid #42424b;
  border-collapse: collapse;
  margin-bottom: 10px;
}
td, th {
  padding: 5px;
}
th, table.decorated tr:first-child td {
  background-color: #e8eff5;
  border: 1px solid #42424b;
}
table.decorated td {
  border: 1px solid #42424b;
  text-align: left;
  vertical-align: top;
}
header {
  background-color: #FFFFFF;
  color: #2c343a;
  margin: 0;
  padding-top: 5px;
  padding-bottom: 5px;
}
header > * {
  margin: auto;
  max-width: 900px;
}
footer {
  background-color: #414d58;
  color: #ffffff;
  font-size: 0.8em;
  margin: 20px 0 0 0;
  padding-bottom: 10px;
  padding-top: 10px;
  position:absolute;
  width: 100%;
  bottom:0;
  left:0;
  height: 70px;
}
footer > * {
  margin: auto;
  max-width: 900px;
}
footer a:link {
  color: #ffffff;
  text-decoration: underline;
}
footer a:visited {
  color: #ffffff;
  text-decoration: underline;
}
footer a:hover {
  color: #ffffff;
  text-decoration: underline;
}
img {
  border: 0 none;
}
label {
  font-weight: normal;
  margin-bottom: 0;
}
.feature {
  background-color: #e8eff5;
  color: #000000;
  margin-bottom: 20px;
  margin-top: 20px;
  padding: 20px;
}
#content {
  margin: auto;
  max-width: 900px;
  padding-bottom: 70px;
}
#subheader {
  background-color: #b9d979;
  margin-bottom: 20px;
}
#subheader-content {
  max-width: 900px;
  color: #2c343a;
  font-family: "Roboto";
  font-size: 1.5em;
  font-style: normal;
  font-weight: 500;
  margin: 0 auto;
  padding-bottom: 10px;
  padding-top: 10px;
}
#subheader-left {
  text-align: left; 
  float: left;
}
#subheader-right {
  text-align: right; 
  float: right;
}
#timer {
  letter-spacing: 2px;
}
#cnrto-platform-blurb {
  color: #b9d979;
}
#cnrto-platform-link {
  color: #b9d979;
}
#cnrto-platform-link:hover {
  text-decoration: none;
}
.warning {
  margin: 5px;
  padding: 5px;
  background-color: #D9C978;
  font-weight: bold;
}
.warning:empty {
  display: none;
}
.clearing {
  height: 1px;
}
#test-wrapper {
  background-color: #FFFFFF;
  min-height:100%;
  position: relative;
}
